import { addData, queryData } from '@/api/common'
import CmdList from '../../Cmd'
export default {
  components: {
    CmdList
  },
  data () {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      previewVisible: false,
      previewImage: '',
      fileList: [],
      fileName: '',
      cmdList: [],
      photo: '',
      faceName: '',
      mapId: '',
      mapList: [],
      cmd: '',
      showEditCmd: false

    }
  },
  created(){
    this.getCmd();
    this.getMap()
  },
  methods: {
    changeCmd(event) {
      this.cmd = event
      this.showEditCmd = true
    },
    // 修改命令集
    editCmd () {
      let formData = this.form.getFieldsValue();
      if (formData.name == undefined) {
        this.$message.error('请先输入人脸名称')
        return;
      }
      let mapId = formData.mapId == undefined ? 0 : formData.mapId
      console.log(mapId)
      this.$refs.cmd.show('编辑人脸命令集', mapId, this.cmd)
    },
    // 新增命令集
    addCmd () {
      let formData = this.form.getFieldsValue();
      if (formData.name == undefined) {
        this.$message.error('请先输入人脸名称')
        return;
      }
      let mapId = formData.mapId == undefined ? 0 : formData.mapId
      this.$refs.cmd.show('编辑人脸命令集', mapId, 0)
    },
    add () {
      this.form.resetFields();
      this.visible = true
      this.cmd = ''
      this.showEditCmd = false
    },
    // showCmd(){
    //   let formData = this.form.getFieldsValue();
    //   if(formData.name == undefined){
    //     this.$message.error('请先输入人脸名称')
    //     return;
    //   }
    //   let mapId = formData.mapId == undefined ? 0 : formData.mapId
    //   this.$refs.cmd.show(formData.name,mapId,this.selectCmd)
    // },
    // 编辑命令集后，获取命令集ID
    getCmdId (cmd) {
      this.getCmd()
      this.form.setFieldsValue({
        cmd: parseInt(cmd)
      });
    },
    getMap () {
      let org = JSON.parse(localStorage.getItem('user')).orgId;
      queryData({ orgId: org }, '/robotMapLib/getRobotMapLibList').then(res => {
        console.log('-----------getMap', res);
        this.mapList = res.result;
      })
    },
    getCmd () {
      let org = JSON.parse(localStorage.getItem('user')).orgId;
      let _this = this;
      queryData({ orgId: org }, '/cmdCollection/getCmdListByOrg').then(res => {
        console.log(res)
        if (res.code == 0) {
          _this.cmdList = res.cmdList
        } else {
          _this.$message.error(res.message)
        }
      });
    },
    handleCancel () {
      this.previewVisible = false
    },
    handlePreview (file) {
      console.log(file);
      this.previewImage = file.url || file.thumbUrl
      this.previewVisible = true
    },
    handleChange ({ fileList }) {
      if (fileList.length > 0) {
        if (fileList[0].status == 'done') {
          var res = fileList[0].response;
          console.log(res)
          if (res.code == 0) {
            this.fileName = res.fileName;
            this.$message.success(`${res.fileName} 上传成功`)
          } else {
            this.$message.error(res.message)
          }
        }
      }
      this.fileList = fileList
    },
    // 下拉框
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },

    handleSubmit() {
      const { form: { validateFields } } = this
      this.confirmLoading = true
      validateFields((errors, values) => {
        if (!errors) {
          var _this = this
          if(this.fileName == ''){
            this.$message.error('请上传人脸图片');
            return;
          }
          setTimeout(() => {
            let org = JSON.parse(localStorage.getItem('user')).orgId;
            // values.cmd = this.cmd == '' ? values.cmd : this.cmd
            addData(Object.assign(values,{org:org,photo:this.fileName}), '/faceManage/addFaceMsg').then(res => {
              if (res.code == 0) {
                _this.$message.success('新建成功');
              } else {
                _this.$message.error(res.message)
              }
              _this.fileList = [];
              _this.visible = false
              _this.confirmLoading = false
              _this.$emit('ok', values)
              // 重置输入框内容
              _this.form.resetFields()
            })
          }, 300)
        } else {
          this.confirmLoading = false
        }
      })
    },
    handleCancel () {
      this.visible = false
    }
  }
}
