import { queryData , addData } from '@/api/common'

export default {
  name: 'addRoleModal',
  data() {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 5 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 }
      },
      visible: false,
      confirmLoading: false,
      roles: [],
      name: '',
      form: this.$form.createForm(this),
      permissions: [],
    }
  },
  methods: {
    add() {
      var param = { role: '' }
      var _this = this;
      queryData(param,'/SysRoleManage/loadResourceMsg')
        .then(res => {
          console.log(res)
          _this.permissions = res.result;
          if(res.code !== 0){
            _this.$message.error(res.message);
          }
  
          this.visible = true
        })
        .catch(err => console.log(err))
    },
    close() {
      this.$emit('close')
      this.visible = false
    },
    handleSubmit() {
      const _this = this
      // 触发表单验证
      this.form.validateFields((err, values) => {
        // 验证表单没错误
        if (!err) {
          _this.confirmLoading = true
          addData(Object.assign(values,{resourceList:_this.permissions}),'/SysRoleManage/addSysRoleMsg').then(res => {
            console.log(res)
            if(res.code == 0){
              _this.$message.success('新建成功');
              _this.visible = false;
              // 重置输入框内容
              _this.form.resetFields();
              this.$emit('ok', values)
            } else {
              _this.$message.error(res.message);
            }
             _this.confirmLoading = false;
          }).catch((err) => console.log(err))
        }
      })
    },
    handleCancel() {
      this.close()
    },
    onChangeCheck(permission) {
      permission.indeterminate =
        !!permission.selected.length && permission.selected.length < permission.actionsOptions.length
      permission.checkedAll = permission.selected.length === permission.actionsOptions.length
    },
    onChangeCheckAll(e, permission) {
      Object.assign(permission, {
        selected: e.target.checked ? permission.actionsOptions.map(obj => obj.value) : [],
        indeterminate: false,
        checkedAll: e.target.checked
      })
    }
  }
}
