import { addData ,queryData  } from '@/api/common'

export default {
  data() {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      editData:{},
      cmdTypeList: [],
      type:0,
    }
  },
  methods: {
    add(record) {
      this.visible = true,
      this.type = parseInt(record.type);
      this.editData = record;
    },
    // 下拉框
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    handleSubmit() {
      const { form: { validateFields } } = this
      this.confirmLoading = true;
      validateFields((errors, values) => {
        if (!errors) {
          var _this = this
          setTimeout(() => {

            // values.type = Number.isInteger(values.type) ? values.type : this.type;
            var param = Object.assign(values,{entityId:this.editData.id})
            addData(param,'/cmdUnitType/save').then(res => {
              if (res.code == 0) {
                _this.$message.success('编辑成功')
              } else {
                _this.$message.error(res.message)
              }
                _this.visible = false
                _this.confirmLoading = false
                _this.$emit('ok', values);
                // 重置输入框内容
                _this.form.resetFields()
            })

          }, 300)
        } else {
          this.confirmLoading = false
        }
      })
    },
    handleCancel() {
      this.visible = false
    }
  }
}
