import { addBusinessQues } from '@/api/ques'

export default {
  data() {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      isPub:false,
      editData:[],
      entityId:''
    }
  },
  methods: {
    onChange(check){
      console.log('check:',check);
    },
    add(record) {
      this.visible = true;
      this.editData = record;
      this.isPub = record.isPub == 1 ? true :false
      this.entityId = record.id;
    },
    handleSubmit() {
      const { form: { validateFields } } = this
      this.confirmLoading = true;
      validateFields((errors, values) => {
        if (!errors) {
          var _this = this
          setTimeout(() => {
            let isPub = ( values.isPub == undefined || values.isPub == false ) ? 0 : 1 ;
            values.isPub = isPub;
            let userObj = JSON.parse(localStorage.getItem('user'));
            let [orgId,user,descn] = [userObj.orgId,userObj.id,1];
            let param = Object.assign(values,{orgId,user,descn},{entityId:this.entityId});
            addBusinessQues(param).then(res => {
              if (res.code == 0) {
                _this.$message.success('编辑成功')
              } else {
                _this.$message.error(res.message)
              }
              _this.visible = false
                _this.confirmLoading = false
                _this.$emit('ok', values);
                _this.entityId = '';
                // 重置输入框内容
                _this.form.resetFields()
            })
            
          }, 300)
        } else {
          this.confirmLoading = false
        }
      })
    },
    handleCancel() {
      this.visible = false
    }
  }
}
