import { getOrgList , getRoleList , addUser} from '@/api/manage';

export default {
  data () {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      confirmLoading: false,

      form: this.$form.createForm(this),
      orgNames:[],
      roleNames:[],
    }
  },
  created(){
    this.handleRoleList();
    this.handleOrgList();
  },
  methods: {
    // 获取角色
    handleRoleList(){
      var _this = this;
      getRoleList().then(res => {
        _this.roleNames = res;
      });
    },
    // 获取单位下的单位
    handleOrgList(){
      var param = {org : 1};
      var _this = this;
      getOrgList(param).then(res => {
        _this.orgNames = res;
      });
    },
    add () {
      this.visible = true
    },
    handleSubmit () {
      const { form: { validateFields } } = this
      this.confirmLoading = true
      validateFields((errors, values) => {
        if (!errors) {
          console.log('values', values)
          var _this = this;
          setTimeout(() => {
            addUser(values).then(res => {
              if(res.code == 0){
                _this.$message.success('新建成功');
                // 重置输入框内容
                _this.form.resetFields();
                
              } else {
                _this.$message.error(res.message)
              }
            });
            this.visible = false
            this.confirmLoading = false
            this.$emit('ok', values)
          }, 300)
        } else {
          this.confirmLoading = false
        }
      })
    },
    handleCancel () {
      this.visible = false
    },
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
  }
}
