import { queryData } from '@/api/common'
import { STable, Ellipsis } from '@/components'
export default {
  components: {
    STable,
    Ellipsis
  },
  data() {
    return {
      orgNames: [],
      robotVersionType: [],
      selectCmd: '',
      robotVersionId: '',
      showEditCmd: false,
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      queryParam: { code: '' },
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },
        {
          title: 'APP名称',
          dataIndex: 'name',
          scopedSlots: { customRender: 'name' }
        },
        {
          title: '版本描述',
          dataIndex: 'contents',
          scopedSlots: { customRender: 'contents' }
        },
        {
          title: '校验码',
          dataIndex: 'code'
        }
      ],
      // // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        if (this.queryParam.code == '') {
          this.queryParam.code = '1|1|1|1'
        }
        return queryData(Object.assign(parameter, this.queryParam), '/RobotVersion/queryRobotVersionMsg').then(res => {
          console.log(res)
          return res.result
        })
      },
      selectedRowKeys: new Set([]),
      options: {
        alert: {
          show: true
        },
        rowSelection: {
          type: 'radio',
          onSelect: this.onSelectChange
        }
      },
      optionAlertShow: false
    }
  },
  created() {
    this.handleOrgList()
  },
  methods: {
    getRobotVersionType(value) {
      // 初始化版本类型
      this.form.setFieldsValue({
        code: ''
      })
      // 初始化列表
      this.queryParam.code = ''
      this.$refs.table.refresh()
      var param = { orgId: value }
      var _this = this
      queryData(param, '/RobotVersion/getRobotVersionType').then(res => {
        console.log(res.result)
        _this.robotVersionType = res.result
        if (res.code !== 0) {
          _this.$message.error(res.message)
        }
      })
    },
    // 获取单位下的单位
    handleOrgList() {
      const id = JSON.parse(localStorage.getItem('user')).orgId
      var param = { org: id }
      var _this = this
      queryData(param, '/userManage/getHrOrgByPid').then(res => {
        _this.orgNames = res.result
        if (res.code !== 0) {
          _this.$message.error(res.message)
        }
      })
    },

    getRobotApp(value) {
      this.queryParam.code = value
      this.$refs.table.refresh()
    },
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    // 选择机器人
    onSelectChange(record, selected, nativeEvent) {
      console.log(record, selected)
      if (selected) {
        this.robotVersionId = record.id
        console.log(this.robotVersionId)
      }
    },
    add() {
      this.form.resetFields()
      this.visible = true
      this.queryParam.code = ''
      this.robotVersionId = ''
      this.robotVersionType = []
    },
    handleSubmit() {
      const {
        form: { validateFields }
      } = this
      var _this = this
      this.confirmLoading = true
      validateFields((errors, values) => {
        if (!errors) {
          var param = {
            robotVersion: this.robotVersionId
          }
          queryData({ ...param, ...values }, '/OrgRobotVersion/addOrgRobotVersionMsg').then(res => {
            if (res.code == 0) {
              _this.$message.success('新建成功')
            } else {
              _this.$message.error(res.message)
            }
            _this.visible = false
            _this.confirmLoading = false
            _this.$emit('ok', values)
            // 重置输入框内容
            _this.form.resetFields()
          })
        } else {
          this.confirmLoading = false
        }
      })
    },
    handleCancel() {
      this.visible = false
    }
  }
}
