import { queryData } from '@/api/common'
import moment from 'moment'
export default {
  data() {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      form: this.$form.createForm(this),
      orgNames: [],
      editData: {}
    }
  },
  created() {
  },
  methods: {
    // 获取该用户下的所属单位
    handleOrgLists() {
      var _this = this
      var orgId = JSON.parse(localStorage.getItem('user')).orgId
      var param = { org: orgId }
      queryData(param, '/userManage/getHrOrgByPid')
        .then(res => {
          _this.orgNames = res.result
        })
    },
    add(record) {
      this.handleOrgLists()
      this.form.resetFields()
      this.editData = record
      this.visible = true
    },
    handleSubmit() {
      const {
        form: { validateFields }
      } = this
      validateFields((errors, values) => {
        if (!errors) {
          var param = {
            code: this.editData.code,
            name: values.name,
            validity: values.validity,
            faceTime: values.faceTime,
            orgId: Number.isInteger(values.orgId)?values.orgId:this.editData.orgId
          }
          var _this = this
          setTimeout(() => {
            queryData(param, '/robotInfo/save').then(res => {
              console.log(res)
              if (res.code == 0) {
                _this.$message.success('修改成功')
              } else {
                _this.$message.error(res.message)
              }
            })
            this.visible = false
            this.$emit('ok', values)
          }, 300)
        }
      })
    }
  }
}
