import { queryData, addData } from '@/api/common'
import { STable } from '@/components'
import { constants } from 'fs'
export default {
  components: {
    STable
  },
  data() {
    return {
      edit:{id :0,name:"",fileName:"",imageName:'',descn:""},
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      appImageVisible:false,
      appImageWebUrl: '',//图片访问地址
      descnVisible:false,
      updataVisible:false,
      fileList: [],
      confirmLoading: false,
      form: this.$form.createForm(this),
      queryParam:{},
      imageList: [{
            "fileName": '123456.png',
            "name": "党建待机（创创）",
        }, {
            "fileName": '123457.png',
            "name": "教育待机（创创）",
        }, {
            "fileName": '123458.png',
            "name": "医疗待机（创创）",
        }, {
            "fileName": '123459.png',
            "name": "政务待机（创创）",
        }],
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },

        {
          title: '机器人名称',
          dataIndex: 'robotName',
          scopedSlots: { customRender: 'robotName' }
        },
        {
          title: '机器人编码',
          dataIndex: 'code',
          scopedSlots: { customRender: 'code' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      data:[],
      options: false,
      optionAlertShow: false
    }
  },
  created() {},
  methods: {
      add(record){
        console.log(record)
        this.visible = true
        if(record.type == "主页"){
            this.descnVisible = true
            this.updataVisible = false
            this.edit.descn = record.descn;
        }else{
            this.updataVisible = true
            this.descnVisible = false
        }
        this.edit.id = record.id;
        this.edit.fileName = record.fileName;
        this.edit.imageName = record.name;
        this.edit.name = record.name;
        this.data = [];
        this.data.push(record);
      },
     filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    handleSubmit() {
      const {
        form: { validateFields }
      } = this
      var _this = this
      this.confirmLoading = true
      validateFields((errors, values) => {
        if (!errors) {
          if(this.edit.name == ''|| this.edit.name == null){
            this.$message.error('懒得名称都不写了？😈');
            return;
          }
          var param = {
            name:this.edit.name,
            descn:this.edit.descn,
            entityId:_this.edit.id,
            fileName:this.edit.fileName
          }
          queryData({ ...param, ...values }, '/appImage/save').then(res => {
            if (res.code == 0) {
              _this.$message.success('修改成功')
              this.edit={id :0,name:"",fileName:"",imageName:'',descn:""}
            } else {
              _this.$message.error(res.message)
            }
            _this.visible = false
            _this.confirmLoading = false
            _this.$emit('ok', values)
            // 重置输入框内容
            _this.form.resetFields()
          })
        } else {
          this.confirmLoading = false
        }
      })
    },
    handleCancel() {
      this.appImageVisible = false
    },
    //公共图片选择列表监听事件
    handleAppImageChange2 (value) {
      this.edit.fileName = value
    },
    //文件预览
    handlePreview(file) {
      console.log(file.url+"/"+ file.thumbUrl)
      this.appImageWebUrl = file.url || file.thumbUrl
      this.appImageVisible = true
    },
    // 删除文件
    handleRemoveFile(file) {
      let _this = this
      queryData({ fileName: file.response.fileName },'/appImage/delFile').then(res => {
        if (res.code == 0) {
          _this.$message.error(res.message)
          return true
        } else {
          _this.$message.error(res.message)
          return false
        }
      })
    },
    //文件长传监听
    handleChange ({ fileList }) {
      if (fileList.length > 0) {
        if (fileList[0].status == 'done') {
          var res = fileList[0].response;
          console.log(res)
          if (res.code == 0) {
            this.edit.fileName = res.fileName;
            this.$message.success(`${res.fileName} 上传成功`)
          } else {
            this.$message.error(res.message)
          }
        }
      }
      this.fileList = fileList
    },
  }
}
