import { STable, Ellipsis } from '@/components'
import { queryData } from '@/api/common'
import editRobotNote from './EditRobotNote'

export default {
  components: {
    STable,
    Ellipsis,
    editRobotNote
  },
  data() {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      columns: [
        {
          title: '序号',
          dataIndex: 'id',
          scopedSlots: { customRender: 'serial' }
        },
        {
          title: '记录日期',
          dataIndex: 'noteDate'
        },
        {
          title: '开机时间',
          dataIndex: 'openTime'
        },
        {
          title: '开机次数',
          dataIndex: 'openCount'
        },
        {
          title: '在线时长',
          dataIndex: 'onlineDuration'
        },
        {
          title: '行走里程',
          dataIndex: 'mileage'
        },
        {
          title: '最后一次心跳时间',
          dataIndex: 'lastOnlineTime'
        },
        {
          title: '业务匹配次数',
          dataIndex: 'buisCount'
        },
        {
          title: '闲聊匹配次数',
          dataIndex: 'chatCount'
        },
        {
          title: '备注',
          dataIndex: 'remark',
          scopedSlots: { customRender: 'remark' }
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 查询参数
      queryParam: {
        noteDate: '',
        robotCode: ''
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      loadData: parameter => {
        var parma = Object.assign(parameter, this.queryParam)
        return queryData(parma, '/RobotNote/queryRobotNote').then(res => {
          console.log(res)
          return res.result
        })
      }
    }
  },
  methods: {
    show (record) {
      this.queryParam.robotCode = record.code
      this.visible = true
      this.$refs.table.refresh(true)
    },
    handleOk () {
      this.$refs.table.refresh(true)
    },
    // 查看运行记录
    editRemark (record) {
      this.$refs.editRobotNote.edit(record)
    },
    onChange (date, dateString) {
      this.queryParam.noteDate = dateString
      this.$refs.table.refresh()
    },
    // 重置
    resetQuery () {
      this.$refs.table.refresh(true)
    },
    searchData () {
      this.$refs.table.refresh()
    },

    handleSubmit () {
      this.visible = false
    }
  }
}
