import { STable } from '@/components'
import { PageView } from '@/layouts'
import EditModal from './modules/EditUser'
import CreateModal from './modules/CreateUser'
import { queryData } from '@/api/common'
const statusMap = {
  0: {
    status: 'default',
    text: '无效'
  },
  1: {
    status: 'processing',
    text: '有效'
  }
}
export default {
  name: 'TableList',
  components: {
    STable,
    PageView,
    EditModal,
    CreateModal
  },
  data() {
    return {
      // 高级搜索 展开/关闭
      advanced: false,
      selectedItems: [],
      // 查询参数
      queryParam: {
        queryOrgId: '',
        userName: ''
      },
      editData: {},
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },
        {
          title: '登录名',
          dataIndex: 'loginName',
          scopedSlots: { customRender: 'loginName' }
        },
        {
          title: '单位名称',
          dataIndex: 'orgName',
          scopedSlots: { customRender: 'orgName' }
        },
        {
          title: '角色名称',
          dataIndex: 'roleNames',
          scopedSlots: { customRender: 'roleNames' }
        },
        {
          title: '用户姓名',
          dataIndex: 'userName',
          scopedSlots: { customRender: 'userName' }
        },
        {
          title: '手机号',
          dataIndex: 'mobile',
          scopedSlots: { customRender: 'mobile' }
        },
        {
          title: '是否生效',
          dataIndex: 'isDel',

          scopedSlots: { customRender: 'isDel' }
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        console.log('user 请求', parameter)
        let id = JSON.parse(localStorage.getItem('user')).orgId

        var orgId = { orgId: id }
        return queryData(Object.assign(parameter, this.queryParam, orgId), '/userManage/queryUserMsg').then(res => {
          console.log(res)
          return res.result
        })
      },
      orgNames: [],
      roleNames: [],
      selectedRowKeys: [],
      selectedRows: [],
      options: {
        alert: false,
        rowSelection: null
      },
      optionAlertShow: false
    }
  },
  created() {
    this.handleOrgList()
    this.handleRoleList()
  },
  computed: {
    title() {
      return this.$route.meta.title
    }
  },
  filters: {
    statusFilter(type) {
      return statusMap[type].text
    },
    statusTypeFilter(type) {
      return statusMap[type].status
    }
  },
  methods: {
    // 获取单位下的单位
    handleOrgList() {
      let id = JSON.parse(localStorage.getItem('user')).orgId
      var _this = this
      queryData({ org: id }, '/userManage/getHrOrgByPid').then(res => {
        console.log(res)
        _this.orgNames = res.result
        if (res.code !== 0) {
          _this.$message.error(res.message)
        }
      })
    },
    tableOption() {
      if (!this.optionAlertShow) {
        this.options = {
          alert: {
            show: true,
            clear: () => {
              this.selectedRowKeys = []
            }
          },
          rowSelection: {
            selectedRowKeys: this.selectedRowKeys,
            onChange: this.onSelectChange,
            getCheckboxProps: record => ({
              props: {
                disabled: record.no === 'No 2', // Column configuration not to be checked
                name: record.no
              }
            })
          }
        }
        this.optionAlertShow = true
      }else{
        this.options = {
          alert: false,
          rowSelection: null
        },
        this.optionAlertShow = false
      }
    },

    onSelectChange(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    // 获取角色
    handleRoleList() {
      console.log('获取角色')
      var _this = this
      var roleId = JSON.parse(localStorage.getItem('user')).roleId
      queryData({ role: roleId }, '/userManage/getAllSysRole').then(res => {
        _this.roleNames = res
      })
    },

    // 处理新增
    handleAddSubmit() {
      this.$refs.table.refresh(true)
    },

    // 重置密码
    handleResetPwd(record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要重置 ${record.loginName}的密码吗?`,
        okText: '重置',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 1000)
            var param = { id: record.id }
            queryData(param, '/userManage/revertPassword')
              .then(res => {
                if (res.code == 0) {
                  _this.$message.success('密码重置成功')
                }
              })
              .catch(() => _this.$message.error(res.message))
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    // 表单重置
    resetQuery() {
      this.queryParam = {queryOrgId: '',
        userName: ''}
      this.$refs.table.refresh(true)
    },
    // 显示状态修改
    handleAddVisible() {
      this.addVisible = true
    },
    handleEditVisible(record) {
      this.editData = record
      this.$refs.editModal.add(record)
    },
    handleChangeOption(value) {
      console.log(`select ${value}`)
    },
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    // 搜索内容
    searchData() {
      this.$refs.table.refresh(true)
    },
    // 删除
    handleDeleteUser(record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要删除 ${record.loginName} 吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var id = { id: [record.id] }
            queryData(id, '/userManage/delUserMsg').then(res => {
              console.log('删除用户返回值：', res)
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.$refs.table.refresh()
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    // 批量删除
    deleteUsers() {
      console.log(this.selectedRowKeys)
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要删除吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var ids = { id: _this.selectedRowKeys }
            queryData(ids, '/userManage/delUserMsg').then(res => {
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.selectedRowKeys = []
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    }
  }
}
