import { addData, queryData } from '@/api/common'
import { STable } from '@/components'
export default {
  components: {
    STable
  },
  data() {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      columns: [
        {
          title: '序号',
          dataIndex: 'id',
          scopedSlots: { customRender: 'serial' }
        },
        {
          title: '问答库名称',
          dataIndex: 'name',
          scopedSlots: { customRender: 'name' }
        },
        {
          title: '所属单位',
          dataIndex: 'orgName',
          scopedSlots: { customRender: 'orgName' }
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 查询参数
      queryParam: {
        name: ''
      },
      code: {},
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      loadData: parameter => {
        var parma = Object.assign(parameter, { code: this.code }, this.queryParam)
        return queryData(parma, '/skillsRobot/getSkillsRobotList').then(res => {
          console.log(res)
          return res.result
        })
      }
    }
  },
  methods: {
    add(record) {
      this.code = record.code
      this.visible = true
      //this.$refs.table.refresh(true)
      //this.isAble = record.isAble == 1 ? true : false
    },
    // 表单重置
    resetQuery() {
      this.queryParam = {}
      this.$refs.table.refresh(true)
    },
    handleOk() {
      this.$refs.table.refresh()
    },
    searchData() {
      this.$refs.table.refresh()
    },
    handleSubmit() {
      this.visible = false
    },
    handleIsAble(checked,record) {
      console.log(checked)
       var _this = this
        var orgId = JSON.parse(localStorage.getItem('user')).orgId
      var param = { entityId: record.id }
      if(checked){
        param.isAble=1
      }else{
        param.isAble=0
      }    

      queryData(param, '/skillsRobot/isAble').then(res => {
        _this.orgNames = res.result
        if (res.code !== 0) {
          _this.$message.error(res.message)
        }
      }).catch((err) => console.log(err))
    },
    deleteSkillsType(record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要删除 ${record.name} 吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 1000)
            var id = { entityId: record.id }
            queryData(id, '/skillsRobot/delete').then(res => {
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    }
  }
}
