import { addData, queryData } from '@/api/common'
import { STable } from '@/components'
export default {
  components: {
    STable
  },
  data() {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      columns: [
        {
          title: '',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },
        {
          title: '记录点名称',
          dataIndex: 'name',
          scopedSlots: { customRender: 'name' }
        },
        {
          title: '记录点坐标',
          dataIndex: 'point',
          scopedSlots: { customRender: 'point' }
        },
        {
          title: '地图姿态',
          dataIndex: 'rotation',
          scopedSlots: { customRender: 'rotation' }
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 查询参数
      queryParam: {
        name: ''
      },
      mapId: 0,
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      loadData: parameter => {
        var parma = Object.assign(parameter, { mapId: this.mapId }, this.queryParam)
        return queryData(parma, '/mapGsPoint/getMapGsPointPage').then(res => {
          console.log(res)
          return res.result
        })
      }
    }
  },
  methods: {
    add(record) {
      this.mapId = record.id
      this.visible = true;
      this.$refs.table.refresh(true)
    },
     // 表单重置
    resetQuery(){
      this.queryParam = {};
      this.$refs.table.refresh(true)
    },
    handleOk() {
      this.$refs.table.refresh()
    },
    searchData() {
      this.$refs.table.refresh()
    },
    handleSubmit() {},
    deleteMapPoint(record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要删除 ${record.mapName} 吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 1000)
            var id = { entityId: record.id }
            queryData(id, '/mapPoint/delete').then(res => {
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    }
  }
}
