import { queryData, addData } from '@/api/common'
import { STable } from '@/components'
export default {
  components: {
    STable
  },
  data() {
    return {
        edit:{triggerId:0,triggerName:"",cmdId:""},
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),     
      cmdList: [],    
      queryParam:{},
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },

        {
          title: '机器人名称',
          dataIndex: 'robotName',
          scopedSlots: { customRender: 'robotName' }
        },
        {
          title: '机器人ID',
          dataIndex: 'id',
          scopedSlots: { customRender: 'id' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      data:[],
      selectedRowKeys: new Set([]),
      selectedRows: [],
      options: {
        alert: {
          show: true
        },
        rowSelection: {        
          onSelect:this.onSelectChange,
          onSelectAll:this.onSelectChangeAll
        }
      },
      optionAlertShow: false
    }
  },
  created() {},
  methods: {  
     filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    //选择机器人
    onSelectChange (record, selected, selectedRows, nativeEvent) {          
        if(selected){
            this.selectedRowKeys.add(record.id)           
        }else{
             this.selectedRowKeys.delete(record.id)
        }
    console.log([...this.selectedRowKeys])  
    },
    onSelectChangeAll(selected, selectedRows, changeRows){    
     if(selected){           
             changeRows.map((value,index)=>{
                 this.selectedRowKeys.add(value.id)   
             })       
        }else{           
             changeRows.map((value,index)=>{
                 this.selectedRowKeys.delete(value.id)   
             })   
        }
    },
   
 //获取命令集
    getCmdList() {
      var _this = this
       var id = JSON.parse(localStorage.getItem('user')).id
       var param={ "orgId": id}
      queryData(param, '/cmdCollection/getCmdListByOrg').then(res => {
       // console.log('命令集', res)
        if (res.code == 0) {
          _this.cmdList = res.cmdList
        } else {
          _this.$message.error(res.message)
        }
      })
    },

    add(record) {
      console.log("----------record")
      console.log(record)
      this.visible = true
      this.edit=record;   
      this.data=[];   
      this.data.push(record);      
      this.getCmdList()
        this.selectedRowKeys.clear(); 
      if(this.$refs.table){
          this.$refs.table.refresh()
       }   
    },

    handleSubmit() {
      const {
        form: { validateFields }
      } = this
      var _this = this     
      this.confirmLoading = true
      validateFields((errors, values) => {
        if (!errors) {
          var param = {
            robotId: [..._this.selectedRowKeys],
            cmdId: '1',
            triggerId:_this.edit.triggerId,
            entityId:_this.edit.id
          }
          queryData({ ...param, ...values }, '/supercmdTriggerRobot/save').then(res => {
            if (res.code == 0) {
              _this.$message.success('新建成功')
            } else {
              _this.$message.error(res.message)
            }
            _this.visible = false
            _this.confirmLoading = false
            _this.$emit('ok', values)
            // 重置输入框内容
            _this.form.resetFields()
          })
        } else {
          this.confirmLoading = false
        }
      })
    },
    handleCancel() {
      this.visible = false
    }
  }
}
