import { STable } from '@/components'
import { PageView } from '@/layouts'
import { queryData } from '@/api/common';
import CreateModel from './modules/CreateCmdType'
import EditModel from './modules/EditCmdType'
const statusMap = {
  1: {
    status: 'processing',
    text: '声音 '
  },
  2: {
    status:'success',
    text: '串口'
  },
  3: {
    status:'warning',
    text: '思岚'
  },
  5: {
    status:'default',
    text: '技能'
  }
}
export default {
  name: 'TableList',
  components: {
    STable,
    PageView,
    CreateModel,
    EditModel
  },
  data () {
    return {
      // 高级搜索 展开/关闭
      advanced: false,
       // 查询参数
      queryParam: {
        name:'',
        type:''
      },
      cmdTypeList:[],
      // 表头
      columns: [
        {
          title: '序号',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },
        {
          title: '命令类型名称',
          dataIndex: 'name',
          scopedSlots: { customRender: 'name' }
        },
        {
          title: '命令名称参数类型',
          dataIndex: 'type',
          scopedSlots: { customRender: 'type' }
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        return queryData(Object.assign(parameter, this.queryParam),'/cmdUnitType/list')
          .then(res => {
            console.log(res);
            return res.result
          })
      },
    }
  },
  computed: {
    title () {
      return this.$route.meta.title
    },
  },
  filters: {
    statusFilter (type) {
      return statusMap[type].text
    },
    statusTypeFilter (type) {
      return statusMap[type].status
    }
  },
  methods: {
    // 下拉框
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    // 处理刷新
    handleRefresh(){
      this.$refs.table.refresh(true)
    },
    // 表单重置
    resetQuery(){
      this.queryParam = {};
      this.$refs.table.refresh(true)
    },
    handleEditVisible(record){
      this.$refs.editModal.add(record);
    },
    // 搜索内容
    searchData(){
      this.$refs.table.refresh(true)
    },
    // 删除
    handleDelete(record){
      var _this = this;
       this.$confirm({
        title: '警告',
        content: `真的要删除 ${record.name} 吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 200)
            var id = {entityId: record.id};
            queryData(id,'/cmdUnitType/delete').then(res => {
              if(res.code == 0){
                 _this.$message.success('删除成功');
                 _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            });
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })
      
    },
  },

}
