import { STable } from '@/components'
import { PageView } from '@/layouts'
import EditRobot from './modules/EditRobot'
import { queryData } from '@/api/common'
import addSkillRobot from './modules/AddSkillRobot'
import ShowType from './modules/showSkillsType'
import addAiuiScene from './modules/AddAiuiScene'
export default {
  name: 'TableList',
  components: {
    STable,
    PageView,
    EditRobot,
    addSkillRobot,
    ShowType,
    addAiuiScene
  },
  data () {
    return {
      // 高级搜索 展开/关闭
      advanced: false,
      addVisible: false,
      editVisible: false,
      orgId: {
        orgId: 1
      },
      selectedItems: [],
      // 查询参数
      queryParam: {
        code: '',
        name: '',
        orgId: '',
        validityDate: ''
      },
      editData: {},
      options: {
        alert: false,
        rowSelection: null
      },
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },
        {
          title: '名称',
          dataIndex: 'name'
        },
        {
          title: '编码',
          dataIndex: 'code'
        },
        {
          title: '单位名称',
          dataIndex: 'orgName'
        },
        {
          title: '地图名称',
          dataIndex: 'mapName'
        },
        {
          title: 'aiui场景',
          dataIndex: 'aiuiScene'
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        const userId = JSON.parse(localStorage.getItem('user')).id
        let orgId
        if (this.queryParam.orgId > 0) {
          orgId = this.queryParam.orgId
        } else {
          orgId = JSON.parse(localStorage.getItem('user')).orgId
        }
        return queryData(
          Object.assign(parameter, this.queryParam, { userId: userId, orgId: orgId }),
          '/robotInfo/list'
        ).then(res => {
          console.log(res)
          return res.result
        })
      },
      orgNames: [],
      roleNames: [],
      selectedRowKeys: [],
    }
  },
  created () {
    this.handleOrgLists()
  },
  computed: {
    title () {
      return this.$route.meta.title
    }
  },
  methods: {
    // 获取该用户下的所属单位
    handleOrgLists () {
      var _this = this
      var orgId = JSON.parse(localStorage.getItem('user')).orgId
      var param = { org: orgId }
      queryData(param, '/userManage/getHrOrgByPid')
        .then(res => {
          _this.orgNames = res.result
          if (res.code !== 0) {
            _this.$message.error(res.message)
          }
        })
        .catch(err => console.log(err))
    },
    onChange (date, dateString) {
      this.queryParam.validityDate = dateString
      console.log(date, dateString)
    },
    onSelectChange (selectedRowKeys) {
      this.selectedRowKeys = selectedRowKeys
    },
    // 表单重置
    resetQuery () {
      this.queryParam = {}
      this.$refs.table.refresh(true)
    },
    // 查看问答库
    selectSkillsType (record) {
      this.$refs.showSkillsType.add(record)
    },
    // 查看问答库
    openSkillsRobot (record) {
      this.$refs.addSkillRobot.show(record)
    },
    // 查看问答库
    openAiui (record) {
      this.$refs.addAiuiScene.show(record)
    },
    filterOption (input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    // 搜索内容
    searchData () {
      this.$refs.table.refresh(true)
    },
    handleOk () {
      this.$refs.table.refresh(true)
    },
    // 删除机器人
    deleteAiui (record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `机器人 <${record.name}>的aiui配置将被移除，确定吗？`,
        okText: '确定',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            queryData({ robotCode: record.code }, '/aiuiRobot/deleteAiuiRobot').then(res => {
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })
    }
  }
}
