import { STable } from '@/components'
import { PageView } from '@/layouts'
import { queryData } from '@/api/common'
import CreateImage from './modules/CreateImage'
import EditAllImage from './modules/EditAllImage'
import EditImage from './modules/EditImage'
export default {
  name: 'TableList',
  components: {
    STable,
    PageView,
    CreateImage,
    EditAllImage,
    EditImage
  },
  data() {
    return {
      appImageList: [
        {
            "typeId": 0,
            "type": "开机",
        }, {
            "typeId": 1,
            "type": "休息",
        }, {
            "typeId": 2,
            "type": "LOGO",
        }, {
            "typeId": 3,
            "type": "主页",
        }
    ],
      // 高级搜索 展开/关闭
      advanced: false,
      // 查询参数
      queryParam: {
      },
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },
        {
          title: '名称',
          dataIndex: 'name'
        },
        {
          title: '文件名称',
          dataIndex: 'fileName'
        },
         {
          title: '照片',
          dataIndex: 'photoUrl',
          scopedSlots: { customRender: 'photoUrl' }
        },
        {
          title: '显示类型',
          dataIndex: 'type'
        },{
          title: '机器人',
          dataIndex: 'robotName'
        },{
          title: '编码',
          dataIndex: 'code'
        },{
          title: '备注',
          dataIndex: 'descn'
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter=> {
        var orgId = JSON.parse(localStorage.getItem('user')).orgId
        console.log('user 请求', parameter)
        var params = { orgId: orgId }
        return queryData(Object.assign(parameter, params, this.queryParam), '/appImage/list').then(res => {
          console.log(res)
          return res.result
        })
      },
      selectedRowKeys: [],
      selectedRows: [],
      options: {
        alert: false,
        rowSelection: null
      },
      optionAlertShow: false
    }
  },
  // computed: {
  //   title() {
  //     return this.$route.meta.title
  //   }
  // },
  created() {
    // this.getCmdList()
  },
  methods: {
    tableOption() {
      if (!this.optionAlertShow) {
        this.options = {
          alert: {
            show: true,
            clear: () => {
              this.selectedRowKeys = []
            }
          },
          rowSelection: {
            selectedRowKeys: this.selectedRowKeys,
            onChange: this.onSelectChange,
            getCheckboxProps: record => ({
              props: {
                disabled: record.no === 'No 2', // Column configuration not to be checked
                name: record.no
              }
            })
          }
        }
        this.optionAlertShow = true
      } else {
        this.options = {
          alert: false,
          rowSelection: null
        }
        this.optionAlertShow = false
      }
    },
    onSelectChange(selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    // 处理刷新
    handleRefresh() {
      this.$refs.table.refresh(true)
    },
    // 表单重置
    resetQuery() {
      this.queryParam = {}
      this.$refs.table.refresh(true)
    },
    handleEditVisible(record) {
      this.$refs.editImage.add(record)
    },
    // 删除
    handleDelete(record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要删除吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var id = { id: [record.id] }
            queryData(id, '/appImage/delete').then(res => {
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    // 批量删除
    handleDeletes() {
      console.log(this.selectedRowKeys)
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要删除吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var ids = { id: _this.selectedRowKeys }
            queryData(ids, '/appImage/delete').then(res => {
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.selectedRowKeys = []
                _this.$refs.table.refresh()
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    }
  }
}
