import { getSkillsList, getRobotSkillByTypeList, deleteQues ,getTotalSkill} from '@/api/ques'
import { PageView } from '@/layouts'
import { STable } from '@/components'
import CreateQues from './modules/CreateQues'
import EditQues from './modules/EditQues'
import AllSkills from './modules/AllSkills'
export default {
  name: 'TableList',
  components: {
    STable,
    PageView,
    CreateQues,
    EditQues,
    AllSkills
  },
  data() {
    return {
      skillsList: [],
      isShowList: false,
      currectSel: 0,
      businessName: '',
      // 表头
      columnList: [
        {
          title: '业务名称',
          dataIndex: 'title',
          scopedSlots: { customRender: 'name' }
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      loadData: parameter => {
        let orgId = JSON.parse(localStorage.getItem('user')).orgId
        var param = { descn: 1, orgId: orgId }
        return getSkillsList(Object.assign(parameter, this.queryParam, param)).then(res => {
          return res.result
        })
      },
      selectedRowKeys: [],
      selectedRows: [],
      options: {
        alert: {
          show: true,
          clear: () => {
            this.selectedRowKeys = []
          }
        },
        rowSelection: {
          selectedRowKeys: this.selectedRowKeys,
          onChange: this.onSelectChange
        }
      },
      optionAlertShow: false
    }
  },
  computed: {
    title() {
      return this.$route.meta.title
    }
  },
  methods: {
    tableOption() {
      if (!this.optionAlertShow) {
        this.options = {
          alert: {
            show: true,
            clear: () => {
              this.selectedRowKeys = []
            }
          },
          rowSelection: {
            selectedRowKeys: this.selectedRowKeys,
            onChange: this.onSelectChange,
            getCheckboxProps: record => ({
              props: {
                disabled: record.no === 'No 2', // Column configuration not to be checked
                name: record.no
              }
            })
          }
        }
        this.optionAlertShow = true
      } else {
        this.options = {
          alert: false,
          rowSelection: null
        }
        this.optionAlertShow = false
      }
    },
    onSelectChange(selectedRowKeys, selectedRows) {
      console.log('selectedRowKeys:', selectedRowKeys)
      console.log('selectedRows:', selectedRows)
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    // 显示全部技能
    showMore(){
      this.$refs.allSkills.add();
      getTotalSkill({robotSkillTypeId:5}).then(res =>{
        console.log(res)
      });
    },
    // 显示技能
    showTable(id) {
      var _this = this
      var param = { skillsId: id }
      getRobotSkillByTypeList(param).then(res => {
        if (res.code == 0) {
          _this.skillsList = res.result
        } else {
          _this.$message.error(res.message)
        }
        console.log(res)
      })
    },
    showList(id, name) {
      this.businessName = name
      this.currectSel = id
      this.isShowList = true
      this.showTable(id)
    },
    handleRefresh() {
      this.$refs.table.refresh(true)
    },

    handleEditVisible(record) {
      this.$refs.editQues.add(record)
    },
    // 删除
    handleDel() {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要删除吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 300)
            var id = { entityIds: _this.selectedRowKeys }
            deleteQues(id).then(res => {
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(err => console.log(err))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    }
  }
}
