import { STable } from '@/components'
import { PageView } from '@/layouts'
import ShowMap from './modules/ShowMap'
import EditMap from './modules/EditMap'
import { queryData } from '@/api/common'

export default {
  name: 'TableList',
  components: {
    STable,
    PageView,
    ShowMap,
    EditMap
  },
  data() {
    return {
      // 高级搜索 展开/关闭
      advanced: false,
      // 查询参数
      queryParam: {
        robotName: '',
        mapName: '',
        queryOrgId: ''
      },
      columns: [
        {
          title: '地图编码',
          dataIndex: 'id',
          scopedSlots: { customRender: 'id' }
        },
        {
          title: '地图名称',
          dataIndex: 'mapName',
          scopedSlots: { customRender: 'mapName' }
        },
        {
          title: '上传时间',
          dataIndex: 'updateDate',
          scopedSlots: { customRender: 'updateDate' }
        },
        {
          title: '上传机器人',
          dataIndex: 'robotName',
          scopedSlots: { customRender: 'robotName' }
        },
        {
          title: '所属单位',
          dataIndex: 'orgName',
          scopedSlots: { customRender: 'orgName' }
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        var userId = JSON.parse(localStorage.getItem('user')).id
        var orgId = JSON.parse(localStorage.getItem('user')).orgId
        return queryData(
          Object.assign(parameter, this.queryParam, { userId: userId, orgId: orgId }),
          '/robotMapLib/list'
        ).then(res => {
          console.log(res)
          return res.result
        })
      },
      orgNames: []
    }
  },
  computed: {
    title() {
      return this.$route.meta.title
    }
  },
  created() {
    this.handleOrgLists()
  },
  methods: {
    // 获取该用户下的所属单位
    handleOrgLists() {
      var _this = this
      var orgId = JSON.parse(localStorage.getItem('user')).orgId
      var param = { org: orgId }
      queryData(param, '/userManage/getHrOrgByPid')
        .then(res => {
          _this.orgNames = res.result
          if (res.code !== 0) {
            _this.$message.error(res.message)
          }
        })
        .catch(err => console.log(err))
    },
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    // 表单重置
    resetQuery() {
      this.queryParam = {}
      this.$refs.table.refresh(true)
    },
    // 搜索内容
    searchData() {
      this.$refs.table.refresh(true)
    },
    handleOk() {
      this.$refs.table.refresh(true)
    },
    handleEditVisible(record) {
      this.$refs.editModal.add(record)
    },
    handleShowMap(record) {
      this.$refs.showMap.add(record)
    }
  }
}
