import { STable } from '@/components'
import EditModal from './modules/EditFace'
import CreateModal from './modules/CreateFace'
import RobotCmd from './modules/RobotCmd'
import EditRobot from './modules/EditRobotcmd'
import { queryData } from '@/api/common'

export default {
  name: 'TableList',
  components: {
    STable,
    EditModal,
    CreateModal,
    RobotCmd,
    EditRobot
  },
  data() {
    return {
      roleId: 0,
      roleName: '',
      mdl: {},
      // 查询参数
      queryParam: { name: '' },
      // 表头
      columns: [
        // {
        //   title: 'id',
        //   dataIndex: 'id'
        // },
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },
        {
          title: '人脸名称',
          dataIndex: 'name'
        },
        {
          title: '文件名称',
          dataIndex: 'photo'
        },
        {
          title: '照片',
          dataIndex: 'photoUrl',
          scopedSlots: { customRender: 'photoUrl' }
        },
        {
          title: '创建时间',
          dataIndex: 'actionDate'
        },
        {
          title: '命令集',
          dataIndex: 'cmdName'
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],

      loadData: parameter => {
        let orgId = JSON.parse(localStorage.getItem('user')).orgId;
        return queryData(Object.assign(parameter, this.queryParam,{org:orgId}),'/faceManage/queryFaceMsg').then(res => {
          return res.result
        })
      }
    }
  },
  methods: {
    handleEdit(record) {
      this.$refs.editModal.edit(record)
    },
    UpdateRobotCmd(){
      this.$message.error('修改')
    },
    // 删除某一个机器人命令
    DelRobotCmd(record){
      console.log(record);
      var _this = this;
      this.$confirm({
        title: '警告',
        content: `真的要删除 ${record.robotCode} 的命令集吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 300)
            var id = {id: [record.id]};
            queryData(id,'/faceManage/delRobotFace').then(res => {
              if(res.code == 0){
                 _this.$message.success('删除成功');
                 _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            });
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })

    },
    // 配置机器人命令集
    handleRobotCmd (record) {
      this.$refs.robotCmd.show(record);
    },
    handleOk() {
      // 新增/修改 成功时，重载列表
      this.$refs.table.refresh(true)
    },
    // 处理重置
    handleReset() {
      this.queryParam.name = ''
      this.$refs.table.refresh()
    },
    // 删除
    handleDel(record){
      var _this = this;
       this.$confirm({
        title: '警告',
        content: `真的要删除 ${record.name} 吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 300)
            var id = {id: [{id:record.id,photo:record.photo}]};
            queryData(id,'/faceManage/delFaceMsg').then(res => {
              if(res.code == 0){
                 _this.$message.success('删除成功');
                 _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            });
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })

    },
  }
}
