import { queryData, addData } from '@/api/common'
import { STable } from '@/components'
import CmdList from '../../Cmd'
export default {
  components: {
    CmdList,
    STable
  },
  data() {
    return {
      showEditCmd: false,
      edit:{triggerId:0,triggerName:"",cmdId:""},
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      confirmLoading: false,
      form: this.$form.createForm(this),
      cmdList: [],
      queryParam:{},
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },

        {
          title: '机器人名称',
          dataIndex: 'robotName',
          scopedSlots: { customRender: 'robotName' }
        },
        {
          title: '机器人ID',
          dataIndex: 'id',
          scopedSlots: { customRender: 'id' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      data:[],
      options: false,
      optionAlertShow: false
    }
  },
  created() {},
  methods: {
    changeCmd(event) {
      this.edit.cmdId = event
      this.showEditCmd = true
      console.log('下拉框：' + this.edit.cmdId)
    },
    // 新增命令集
    addCmd () {
      this.$refs.cmd.show('传感器', '', 0)
    },
    // 修改命令集
    editCmd () {
      console.log('修改：' + this.edit.cmdId)
      this.$refs.cmd.show('传感器', '', this.edit.cmdId)
    },
    // 编辑命令集后，获取命令集ID
    getCmdId (cmd) {
      this.getCmdList()
      this.edit.cmdId = cmd
      this.form.setFieldsValue({
        cmdId: parseInt(cmd)
      });
    },
     filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
 //获取命令集
    getCmdList() {
      var _this = this
       var orgId = JSON.parse(localStorage.getItem('user')).orgId
       var param={ "orgId": orgId}
      queryData(param, '/cmdCollection/getCmdListByOrg').then(res => {
       // console.log('命令集', res)
        if (res.code == 0) {
          _this.cmdList = res.cmdList
        }
      })
    },

    add (record) {
      console.log(record)
      if (record.cmdId != '' && record.cmdId != null) {
        this.showEditCmd = true
      } else {
        this.showEditCmd = false
      }
      console.log(this.showEditCmd)
      this.visible = true
      this.edit = record;
      this.data = [];
      this.data.push(record);
      this.getCmdList()
      if (this.$refs.table) {
        this.$refs.table.refresh()
      }
    },

    handleSubmit() {
      const {
        form: { validateFields }
      } = this
      var _this = this
      this.confirmLoading = true
      validateFields((errors, values) => {
        if (!errors) {
          var param = {
            cmdId: '1',
            triggerId:_this.edit.triggerId,
            entityId:_this.edit.id
          }
          queryData({ ...param, ...values }, '/supercmdTriggerRobot/save').then(res => {
            if (res.code == 0) {
              _this.$message.success('修改成功')
            } else {
              _this.$message.error(res.message)
            }
            _this.visible = false
            _this.confirmLoading = false
            _this.$emit('ok', values)
            // 重置输入框内容
            _this.form.resetFields()
          })
        } else {
          this.confirmLoading = false
        }
      })
    },
    handleCancel() {
      this.visible = false
    }
  }
}
