import { queryData, addData } from '@/api/common'
export default {
  data() {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
      visible: false,
      form: this.$form.createForm(this),
      orgNames: [],
      roleNames: [],
      scene: '',
      descn: ''
    }
  },
  methods: {
    add() {
      this.visible = true
    },
    handleSubmit() {
      const {
        form: { validateFields }
      } = this
      validateFields((errors, values) => {
        if (!errors) {
          var _this = this
          setTimeout(() => {
            addData(values, '/aiuiScene/add').then(res => {
              if (res.code === 0) {
                _this.$message.success('新建成功')
                // 重置输入框内容
                _this.form.resetFields()
                _this.$emit('ok', values)
              } else {
                _this.$message.error(res.message)
              }
              this.visible = false
            })
          })
        }
      })
    }
  }
}
