import { queryData } from '@/api/common'
import DetailList from '@/components/tools/DetailList'
const DetailListItem = DetailList.Item
export default {
  components: {
    DetailList,
    DetailListItem,
  },
  data() {
    return {
      form: this.$form.createForm(this),
      expandedKeys: [],
      searchValue: '',
      autoExpandParent: true,
      orgTree: [],
      orgList: [],
      comInfo:[],
      pid:'',
      isSelect:false,
      addVisible:false,
      editVisible:false,
      addForm:{
        name:'',
        contact:'',
        contactWay:'',
        descn:''
      },
      editForm:{
      },
      labelCol: {
        xs: { span: 24 },
        sm: { span: 7 }
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 13 }
      },
    }
  },
  beforeMount() {
    this.getDateTree();
  },
  methods: {
    // 新增
    handleAdd(){
      const { form: { validateFields } } = this;
      var _this = this;
      validateFields((errors, values) => {
        if (!errors) {
          console.log('values', values)
        }
        var param = {pid:this.pid,...values.addForm};
        console.log(param);
        
        queryData(param,'/hrOrg/save').then(res => {
           if(res.code == 0){
             _this.$message.success('新建成功');
             _this.addVisible = false;
             _this.form.resetFields();
             _this.getDateTree();
           } else {
              _this.$message.error(res.message);
           }
        });
        
      });
    },
    // 编辑
    handleEdit(){
      const { form: { validateFields } } = this;
      var _this = this;
      validateFields((errors, values) => {
        if (!errors) {
          console.log('values', values)
        }
        var param = {entityId:this.pid,...values.editForm};
        console.log(param);
        
        queryData(param,'/hrOrg/save').then(res => {
           if(res.code == 0){
             _this.$message.success('编辑成功');
             _this.editVisible = false;
             _this.form.resetFields();
             _this.getDateTree();
             _this.getComInfo(_this.pid);
           } else {
              _this.$message.error(res.message);
           }
        });
        
      });
    },
    // 删除
    handleDelete(){
      var _this = this;
      this.$confirm({
        title: '警告',
        content: `真的要删除该单位吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var param = {entityId:_this.pid};
            console.log(param);
            queryData(param,'/hrOrg/delete').then(res => {
              console.log(res)
              if(res.code == 0){
                _this.$message.success('删除成功');
                _this.getDateTree();
              } else {
                _this.$message.error(res.message)
              }
            });
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })
    },
    // 处理显隐
    handleAddVisible(){
      if(this.pid != '' ) {
        this.addVisible = true;
      }
    },
    // 编辑
    handleEditVisible(){
      this.editVisible = true;
      this.editForm = this.comInfo;
    },
    //   获取树形结构数据
    getDateTree() {
      var id = JSON.parse(localStorage.getItem('user')).id;
      var _this = this
      queryData({ userid: id },'hrOrg/list').then(res => {
        console.log(res);
        if(res.code == 0){
          _this.orgTree = res.result
          _this.getOrgList(res.result);     
          var expandedKeys=[];
           res.result.map((value,index)=>{
             if(value.children){
               var item=value.children;
               item.map((value,index)=>{
                  expandedKeys.push(value.key);
               })
             }else{
                expandedKeys.push(value.key);
             }             
           })
           _this.expandedKeys =expandedKeys;
      
        }
      })
    },
    //   获取用于搜索的list数组
    getOrgList(data) {
      for (let i = 0; i < data.length; i++) {
        const node = data[i]
        const key = node.key;
        const title = node.title;
        const orgid = node.orgId;
        const pid = node.pid;
        this.orgList.push({ key, title: title, orgid : orgid, pid : pid })
        if (node.children) {
          this.getOrgList(node.children)
        }
      }
    },
    // 获取当前点击单位
    onSelect(key){
      this.getComInfo(key[0]);
     
    },
    getComInfo(pid){
      var param = {orgId:pid}; 
      var _this = this;
      queryData(param,'/hrOrg/getOrgMessage').then(res => {
        console.log(res);
        if(res.code == 0){
          _this.comInfo  = Object(res.hrOrg);
          if(Object.keys(_this.comInfo).length){
            _this.isSelect = true;
            _this.pid = pid;
          } else {
            _this.isSelect = false;
          }
        } 
      });
    },
    onExpand(expandedKeys) {
      //console.log(expandedKeys);
      this.expandedKeys = expandedKeys
      this.autoExpandParent = false
    },
    // 搜索
    onChange(e) {
      const value = e.target.value

      const expandedKeys = this.orgList
        .map(item => {
          if (item.title.indexOf(value) > -1) {
            return this.getParentKey(item.title, this.orgTree)
          }
          return null
        })
        .filter((item, i, self) => item && self.indexOf(item) === i)
      Object.assign(this, {
        expandedKeys,
        searchValue: value,
        autoExpandParent: true
      })
    },
    // 定位树形结构的数据
    getParentKey(title, tree) {
      let parentKey = 0
      for (let i = 0; i < tree.length; i++) {
        const node = tree[i]
        if (node.children) {
          if (node.children.some(item => item.title === title)) {
            parentKey = node.key
          } else if (this.getParentKey(title, node.children)) {
            parentKey = this.getParentKey(title, node.children)
          }
        }
      }
      return parentKey
    }
  }
}
