import { STable } from '@/components'
import { PageView } from '@/layouts'
import EditRobot from './modules/EditRobot'
import CreateRobot from './modules/CreateRobot'
import { queryData } from '@/api/common'

export default {
  name: 'TableList',
  components: {
    STable,
    PageView,
    EditRobot,
    CreateRobot
  },
  data () {
    return {
      // 高级搜索 展开/关闭
      advanced: false,
      addVisible: false,
      editVisible: false,
      // orgId: this.$store.getters.orgId
      orgId: {
        orgId:1
      },
      selectedItems: [],
       // 查询参数
      queryParam: {
        code:'',
        name:'',
        orgId:'',
        validityDate:'',
      },
      editData:{}, 
      options: {
          alert: false,
          rowSelection: null
      },
      // userId:localStorage.getItem('userId')
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },
        
        {
          title: '机器人名称',
          dataIndex: 'name',
          scopedSlots: { customRender: 'name' }
        },
        {
          title: '机器人编码',
          dataIndex: 'code',
          scopedSlots: { customRender: 'code' }
        },
        {
          title: '单位名称',
          dataIndex: 'orgName',
          scopedSlots: { customRender: 'orgName' },

        },
        {
          title: '地图名称',
          dataIndex: 'mapName',
          scopedSlots: { customRender: 'mapName' }
        },
        {
          title: '有效截止时间',
          dataIndex: 'validityDate',
          scopedSlots: { customRender: 'validityDate' }
        },
        {
          title: '人脸失效时间(分钟)',
          dataIndex: 'faceTime',
          scopedSlots: { customRender: 'faceTime' }
        },
        
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        var userId = JSON.parse(localStorage.getItem('user')).id;
        return queryData(Object.assign(parameter, this.queryParam,{userId:userId}),'/robotInfo/list')
          .then(res => {
            console.log(res);
            return res.result
          })
      },
      orgNames:[],
      roleNames:[],
      selectedRowKeys: [],
      selectedRows: [],
    }
  },
  created(){
    this.handleOrgLists();
  },
  computed: {
    title () {
      return this.$route.meta.title
    },
    
  },
  methods: {
    // 获取该用户下的所属单位
    handleOrgLists(){
      var _this = this;
      var id = localStorage.getItem('userId');
      var param = { userId : id }
      queryData(param,'/robotInfo/getOrglist').then(res => {
        _this.orgNames = res.result;
        if(res.code !== 0){
          _this.$message.error(res.message)
        }
          
      }).catch( (err) => console.log(err))
    },
    onChange(date, dateString) {
      this.queryParam.validityDate = dateString;
      console.log(date, dateString);
    },
    onSelectChange (selectedRowKeys, selectedRows) {
      this.selectedRowKeys = selectedRowKeys
      this.selectedRows = selectedRows
    },
    // 表单重置
    resetQuery(){
      this.queryParam = {};
      this.$refs.table.refresh(true)
    },
    // 显示状态修改
    handleAddVisible(){
      this.addVisible = true;
    },
    handleEditVisible(record){
      this.editData = record;
      console.log(record);
      this.$refs.editModal.add(record);
      this.editVisible = true;
    },
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    // 搜索内容
    searchData(){
      this.$refs.table.refresh(true)
    },
    handleOk(){
      this.$refs.table.refresh(true)
    },
    // 删除
    handleDelete(record){
      var _this = this;
       this.$confirm({
        title: '警告',
        content: `真的要删除 ${record.name} 吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var id = {entityId: record.id};
            queryData(id,'/robotInfo/delete').then(res => {
              console.log('删除返回值：',res);
              if(res.code == 0){
                 _this.$message.success('删除成功');
                 _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            });
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })
      
    },
  }

}
