import { STable } from '@/components'
import { PageView } from '@/layouts'
import EditRobot from './modules/EditRobot'
import { queryData } from '@/api/common'
import ShowType from './modules/showSkillsType'
import ShowRobotNote from './modules/showRobotNote'
import { uploadUrl } from '@/api/upload'

export default {
  name: 'TableList',
  components: {
    STable,
    PageView,
    EditRobot,
    ShowType,
    ShowRobotNote
  },
  data () {
    return {
      // 高级搜索 展开/关闭
      advanced: false,
      addVisible: false,
      editVisible: false,
      orgId: {
        orgId: 1
      },
      selectedItems: [],
      // 查询参数
      queryParam: {
        code: '',
        name: '',
        orgId: '',
        validityDate: ''
      },
      editData: {},
      options: {
        alert: false,
        rowSelection: null
      },
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },
        {
          title: '名称',
          dataIndex: 'name'
        },
        {
          title: '编码',
          dataIndex: 'code'
        },
        {
          title: '单位名称',
          dataIndex: 'orgName'
        },
        {
          title: '地图名称',
          dataIndex: 'mapName'
        },
        {
          title: '最后一次在线时间',
          dataIndex: 'lastLoginTime'
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        let userId = JSON.parse(localStorage.getItem('user')).id
        let orgId
        if (this.queryParam.orgId > 0) {
          orgId = this.queryParam.orgId
        } else {
          orgId = JSON.parse(localStorage.getItem('user')).orgId
        }
        return queryData(
          Object.assign(parameter, this.queryParam, { userId: userId, orgId: orgId }),
          '/robotInfo/list'
        ).then(res => {
          console.log(res)
          return res.result
        })
      },
      orgNames: [],
      roleNames: [],
      selectedRowKeys: [],
      url: uploadUrl + 'robotInfo/excel?robot='
    }
  },
  created () {
    this.handleOrgLists()
  },
  computed: {
    title () {
      return this.$route.meta.title
    }
  },
  methods: {
    // 获取该用户下的所属单位
    handleOrgLists () {
      var _this = this
      var orgId = JSON.parse(localStorage.getItem('user')).orgId
      var param = { org: orgId }
      queryData(param, '/userManage/getHrOrgByPid')
        .then(res => {
          _this.orgNames = res.result
          if (res.code !== 0) {
            _this.$message.error(res.message)
          }
        })
        .catch(err => console.log(err))
    },
    onChange (date, dateString) {
      this.queryParam.validityDate = dateString
      console.log(date, dateString)
    },
    onSelectChange (selectedRowKeys) {
      this.selectedRowKeys = selectedRowKeys
    },
    // 表单重置
    resetQuery () {
      this.queryParam = {}
      this.$refs.table.refresh(true)
    },
    // 显示状态修改
    handleAddVisible () {
      this.addVisible = true
    },
    // 查看问答库
    selectSkillsType (record) {
      this.$refs.showSkillsType.add(record)
    },
    // 查看运行记录
    showRobotNote (record) {
      this.$refs.showRobotNote.show(record)
    },
    handleEditVisible (record) {
      // this.editData = record;
      this.$refs.editModal.add(record)
      this.editVisible = true
    },
    filterOption (input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    // 搜索内容
    searchData () {
      this.$refs.table.refresh(true)
    },
    handleOk () {
      this.$refs.table.refresh(true)
    },
    // 初始化机器人
    handleRecovery (record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `机器人 <${record.name}>将删除后台所有配置，但是会保留自定义库。是否确定？`,
        okText: '确定',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var id = { code: record.code }
            queryData(id, '/robotInfo/recoveryRobot').then(res => {
              console.log('机器人初始化返回值：', res)
              if (res.code == 0) {
                _this.$message.success('初始化成功')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })
    },
    // 删除机器人
    handleDelete (record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `机器人 <${record.name}>信息将被移除，并且信息无法找回，是否确定？`,
        okText: '确定',
        okType: 'danger',
        cancelText: '取消',
        onOk () {
          console.log('OK')
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var id = { code: record.code }
            queryData(id, '/robotInfo/delete').then(res => {
              console.log('移除机器人：', res)
              if (res.code == 0) {
                _this.$message.success('移除成功')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel () {
          console.log('Cancel')
        }
      })
    }
  }
}
