import { STable, Ellipsis } from '@/components'
import UpdateExcel from './modules/UpdateExcel'
import EditChat from './modules/EditChat'
import CreateSkillsRobot from './modules/CreateSkillsRobot'
import { queryData } from '@/api/common'
export default {
  components: {
    STable,
    Ellipsis,
    UpdateExcel,
    EditChat,
    CreateSkillsRobot
  },
  watch: {
    code(values) {
      if (values != '') {
        this.$refs.table.refresh(true)
        this.formVisible = true
      } else {
        this.formVisible = false
      }
    }
  },
  data() {
    return {
      selectedItems: [],
      // 查询参数
      code: '',
      formVisible: false,
      // 表头
      columns: [
        {
          title: '#',
          dataIndex: 'no',
          scopedSlots: { customRender: 'serial' }
        },
        {
          title: '标题',
          dataIndex: 'title',
          scopedSlots: { customRender: 'name' }
        },
        {
          title: '回答内容',
          dataIndex: 'contents',
          scopedSlots: { customRender: 'contents' }
        },
        {
          title: '网页或图片',
          dataIndex: 'url',
          scopedSlots: { customRender: 'url' }
        },
        {
          title: '状态',
          dataIndex: 'isAble',
          scopedSlots: { customRender: 'isAble' }
        },
        {
          title: '动作集',
          dataIndex: 'cmdName'
        },
        {
          title: '操作',
          dataIndex: 'action',
          scopedSlots: { customRender: 'action' }
        }
      ],
      // 加载数据方法 必须为 Promise 对象
      loadData: parameter => {
        return queryData(Object.assign(parameter, { code: this.code, type: 4 }), '/skills/getSkillsList').then(res => {
          return res.result
        })
      },
      selectedRowKeys: [],
      options: {
        alert: false,
        rowSelection: null
      },
      optionAlertShow: false,
      robotList: [] //机器人列表
    }
  },
  created() {
    this.handlerobotList()
  },
  methods: {
    // 获取单位下的单位
    handlerobotList() {
      const orgId = JSON.parse(localStorage.getItem('user')).orgId
      var _this = this
      queryData({ orgId: orgId }, '/robotInfo/loadRobotListByOrgNoPage').then(res => {
        _this.robotList = res.result
      })
    },
    // 回掉函数
    handleAddSubmit() {
      this.$refs.table.refresh(true)
    },
    // 表单重置
    resetQuery() {
      this.code = ''
      this.formVisible = false
      this.$refs.table.refresh(true)
    },
    //修改私有库问题
    handleEditVisible(record) {
      this.$refs.EditChat.add(record)
    },
    //下拉框
    filterOption(input, option) {
      return option.componentOptions.children[0].text.toLowerCase().indexOf(input.toLowerCase()) >= 0
    },
    // 删除
    deleteSkills(record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要删除 ${record.title} 吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var id = { entityIds: [record.id] }
            queryData(id, '/skills/delete').then(res => {
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    // 批量删除
    deleteSkillsIds() {
      console.log(this.selectedRowKeys)
      var _this = this
      this.$confirm({
        title: '警告',
        content: `真的要删除吗?`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            var ids = { entityIds: [_this.selectedRowKeys][0] }

            queryData(ids, '/skills/delete').then(res => {
              if (res.code == 0) {
                _this.$message.success('删除成功')
                _this.selectedRowKeys = []
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    //清空动作集
    delCmd(record) {
      var _this = this
      this.$confirm({
        title: '警告',
        content: ` <${record.title}> 将不再使用 <${record.cmdName}> 动作集`,
        okText: '删除',
        okType: 'danger',
        cancelText: '取消',
        onOk() {
          // 在这里调用删除接口
          return new Promise((resolve, reject) => {
            setTimeout(Math.random() > 0.5 ? resolve : reject, 500)
            queryData({ entityId: record.skillsOptionId }, '/skillsOption/deleteCmd').then(res => {
              if (res.code == 0) {
                _this.$message.success('已解除关联')
                _this.$refs.table.refresh(true)
              } else {
                _this.$message.error(res.message)
              }
            })
          }).catch(() => console.log('Oops errors!'))
        },
        onCancel() {
          console.log('Cancel')
        }
      })
    },
    tableOption() {
      if (!this.optionAlertShow) {
        this.options = {
          alert: {
            show: true,
            clear: () => {
              this.selectedRowKeys = []
            }
          },
          rowSelection: {
            selectedRowKeys: this.selectedRowKeys,
            onChange: this.onSelectChange,
            getCheckboxProps: record => ({
              props: {
                disabled: record.no === 'No 2',
                name: record.no
              }
            })
          }
        }
        this.optionAlertShow = true
      } else {
        this.options = {
          alert: false,
          rowSelection: null
        }
        this.optionAlertShow = false
      }
    },

    onSelectChange(selectedRowKeys) {
      this.selectedRowKeys = selectedRowKeys
    },
    deriveExcel() {
      this.$message.success('别着急快好了😭')
    }
  }
}
